// This file will be generated by flutter gen-l10n
// For now, we create a simple version for development
class AppLocalizations {
  final Locale locale;
  
  AppLocalizations(this.locale);
  
  static AppLocalizations? of(BuildContext context) {
    return Localizations.of<AppLocalizations>(context, AppLocalizations);
  }
  
  static const LocalizationsDelegate<AppLocalizations> delegate = _AppLocalizationsDelegate();
  
  // Add getter methods for all strings
  String get appTitle => 'OSRS Companion';
  String get home => 'Home';
  String get quests => 'Quests';
  String get skills => 'Skills';
  String get calculators => 'Calculators';
  String get moneyMaking => 'Money Making';
  String get geTracker => 'GE Tracker';
  String get xpGoals => 'XP Goals';
  String get favorites => 'Favorites';
  String get settings => 'Settings';
  String get premium => 'Premium';
  String get search => 'Search';
  String get filter => 'Filter';
  String get all => 'All';
  String get welcomeMessage => 'Welcome to OSRS Companion';
  String get selectLanguage => 'Select Your Language';
  String get continue_ => 'Continue';
}

class _AppLocalizationsDelegate extends LocalizationsDelegate<AppLocalizations> {
  const _AppLocalizationsDelegate();
  
  @override
  bool isSupported(Locale locale) {
    return ['en', 'pt', 'es', 'de'].contains(locale.languageCode);
  }
  
  @override
  Future<AppLocalizations> load(Locale locale) async {
    return AppLocalizations(locale);
  }
  
  @override
  bool shouldReload(_AppLocalizationsDelegate old) => false;
}
